import React, { useState } from "react";

export default function SearchBar({onSearch}) {

  const [city, setCity] = useState("")

  const changeHandler = (e) => {
    setCity(e.target.value)
  }

  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      onSearch("Cairns");
    }}>
      <input
        type="text"
        onChange = {changeHandler}
        placeholder="Ciudad..."
      />
      <input type="submit" value="Agregar" />
    </form>
  );
}
